mapper.select:
SELECT BOARD_NO AS boardNo, BOARD_TITLE AS boardTitle, BOARD_CONTENT AS boardContent, BOARD_HIT AS boardHit, BOARD_STATE AS boardState, BOARD_RDATE AS boardRdate, BOARD_TYPE AS boardType, USER_ID AS userId FROM BOARD WHERE BOARD_TYPE = 1 AND BOARD_STATE = 1
AND BOARD_TITLE LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY BOARD_NO DESC limit #{start},#{perPage}

mapper.update:
UPDATE BOARD
	 SET BOARD_HIT = BOARD_HIT + 1
 WHERE BOARD_NO = #{boardNo}

	UPDATE BOARD
     SET BOARD_TITLE = #{boardTitle},
         BOARD_CONTENT = #{boardContent}
   WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
   SET BOARD_STATE = '2'
 WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT BOARD_NO AS boardNo, (SELECT COUNT(*) FROM QCOMMENT q WHERE q.BOARD_NO = b.BOARD_NO AND q.QCOMMENT_STATE = 1) AS qCommentCount, BOARD_TYPE AS boardType, BOARD_TITLE AS boardTitle, BOARD_CONTENT AS boardContent, BOARD_RDATE AS boardRdate, DATE_FORMAT(BOARD_RDATE, '%Y.%m.%d %H:%i') AS formattedBoardRdate, u.USER_ID AS userId FROM BOARD b JOIN USER u ON b.USER_ID = u.USER_ID WHERE b.BOARD_STATE = 1 AND b.BOARD_TYPE = 2
AND b.BOARD_TITLE LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY BOARD_NO DESC LIMIT #{start}, #{perPage}

SELECT BOARD_NO AS boardNo, (SELECT COUNT(*) FROM ECOMMENT e WHERE e.BOARD_NO = b.BOARD_NO AND e.ECOMMENT_STATE = 1) AS eCommentCount, BOARD_TYPE AS boardType, BOARD_TITLE AS boardTitle, BOARD_CONTENT AS boardContent, BOARD_HIT AS boardHit, BOARD_STATE AS boardState, BOARD_RDATE AS boardRdate, DATE_FORMAT(BOARD_RDATE, '%Y.%m.%d %H:%i') AS formattedBoardRdate, u.USER_ID AS userId FROM BOARD b JOIN USER u ON b.USER_ID = u.USER_ID WHERE b.BOARD_STATE = 1 AND b.BOARD_TYPE = 3
AND b.BOARD_TITLE LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY BOARD_NO DESC LIMIT #{start}, #{perPage}
