mapper.update:
    UPDATE COMMENT
			 SET COMMENT_CONTENT = #{commentContent},
					 COMMENT_RATING  = #{commentRating}
     WHERE COMMENT_NO = #{commentNo}

UPDATE COMMENT
   SET COMMENT_STATE = '2'
 WHERE COMMENT_NO = #{commentNo}

UPDATE COMMENT
   SET COMMENT_STATE = 2
 WHERE COMMENT_NO = #{commentNo}
