mapper.update:
UPDATE COMMENT_RATING
			 SET RATING = #{rating}
 WHERE COMMENT_NO = #{commentNo} 
   AND USER_ID = #{userId} 
   AND ISBN = #{isbn}

mapper.select:
SELECT * FROM COMMENT_RATING 
	 WHERE COMMENT_NO = #{commentNo} 
	   AND ISBN = #{isbn}

SELECT COUNT(*) FROM COMMENT_RATING 
WHERE COMMENT_NO = #{commentNo} 
	AND USER_ID = #{userId} 
	AND ISBN = #{isbn}
